import React from 'react';
import { Link } from 'react-router-dom';
import { FiArrowLeft } from 'react-icons/fi';

import "../style.css"
import Card from "../../components/Card"
import {votos} from '../home/index';

export default function Result(){
    
    let winner = Math.max(votos);

    function handleRanking(rank){
        
        let comparativeVotes = votos.map(v =>{ return v })

        if(rank = winner)
          return <span>1º</span>
    
      return <p>Dale</p>
    }

    

    return(
        <div className="body">  
            
            <Link to="/" onClick={() =>{votos.splice(0,6)}}> <FiArrowLeft/>Voltar ao início</Link>
            

            <div className='container-flex-result'>
                <Card id="card-albert">
                    <span id="span-albert">Albert Einstein</span>
                    <p id="ranking">Colocação:{handleRanking(votos[0])} </p>
                
                </Card>
                    
                <Card id="card-marie">
                    <span id="span-marie">Marie Curie</span>
                    <p id="ranking">Colocação:{handleRanking(votos[1])} </p>                
                
                </Card>
                    
                <Card id="card-newton">
                    <span id="span-newton">Isaac Newton</span>
                    <p id="ranking">Colocação:{handleRanking(votos[2])} </p>                
                
                </Card>
                    
                <Card id="card-copernico">
                    <span id="span-copernico">Nicolau Copernico</span>
                    <p id="ranking">Colocação:{handleRanking(votos[3])} </p>                
                
                </Card>
                    
                <Card id="card-null">
                    <span id="span-null">Nulo</span>
                    <span>Nº de votos:{votos[5]}</span>
                
                </Card>
                
                <Card id="card-white">
                    <span id="span-white">Branco</span>
                    <span>Nº de votos:{votos[6]}</span>
                
                </Card>

                
            </div>
        </div>      
    );

}
