        <div className="body">    
            <div className="container-flex">
            <Card id="card-albert">
                <span id="span-albert">Albert Einstein</span>
                <span>NÂº de votos:{counter}</span>
                <button onClick={() => setCounter(counter + 1)}>opa</button>

            </Card>
            <Card id="card-marie"/>
            <Card id="card-newton"/>
            <Card id="card-copernico"/>
          
            
            </div>
       
       <button onClick={armazenarVoto(counter)}>dale</button>
       
       </div>

        
    );
};
