import React from 'react';
import { Link } from 'react-router-dom'
import { FiArrowLeft } from 'react-icons/fi'
import '../style.css';
import Card from '../../components/Card'
import { votos } from '../home/index'
import { useState } from 'react';

export default function Result(){

    
    
    let total = 0;
    
    let ranks = ["1º","2º","3º","4º"];

    let ordelyVotos = votos.sort((a,b) =>{
        return(a<b);
    })

    for(let i=0;i<=3;i++){
        total += votos[i];
    }

    console.log(ordelyVotos,total)

     

    const clearArray = () =>{votos.splice(0,4)}

    const handleRank = (valor) =>{
  
   
        if(valor = ordelyVotos[0]){return <p>{ranks[0]}</p>}
        if(valor = ordelyVotos[1]){return ranks[1]}
        if(valor = ordelyVotos[2]){return ranks[2]}
        if(valor = ordelyVotos[3]){return ranks[3]}


        for(let cont=0 ; cont<votos.length; cont++){
                    
            if(votos[cont] = ordelyVotos[0]){return <span>{ranks[cont]}</span>}
        
            votos[cont] = ordelyVotos[1] ? <span>{ranks[cont]}</span> : ""
            votos[cont] = ordelyVotos[2] ? <span>{ranks[cont]}</span> : ""
            votos[cont] = ordelyVotos[3] ? <span>{ranks[cont]}</span> : ""
        }
    }

   
    return(
        <div className="body">  
            
        <Link id="back-to-home" onClick={clearArray()} to="/"> <FiArrowLeft/>  Voltar para o começo</Link>
            

            <div className='container-flex'>
                <Card id="card-albert">
                    <span id="span-albert">Albert Einstein</span>
                    <span>Posição:{handleRank(votos[0])} </span>
                </Card>

                <Card id="card-marie">
                    <span id="span-marie">Marie Curie</span>

                </Card>

                <Card id="card-newton">
                    <span id="span-newton">Isaac Newton</span>

                </Card>

                <Card id="card-copernico">
                    <span id="span-copernico">Nicolau Copernico</span>
                </Card>

                <Card id="card-null">

                </Card>

                <Card id="card-white">

                </Card>
                
            </div>
        </div>      
    );


}
