import React,{ useState } from 'react';
import { Link } from 'react-router-dom';



import './style.css';


export default function Home(){
    const [counter, setCounter] = useState(0);
    const [counter1, setCounter1] = useState(0)
    const [counter2, setCounter2] = useState(0);
    const [counter3, setCounter3] = useState(0);


const increment = () =>{

    setCounter(counter + 1);
    setCounter1(counter1 + 1);
    setCounter2(counter2 + 1);
    setCounter3(counter3 + 1);
}
    
    return (
            <div className="container-flex">
                <div className="card-albert">
                    <span>Albert E.</span><br/>
                    <span>Contagem de votos: {counter}</span>
                    <button className="button" onClick={() => setCounter(counter + 1)}>Votar</button>
                </div>

                <div className="card-copernico">
                    <span>Nicolau Copernico</span><br/>
                    <span>Contagem de votos: {counter1}</span>
                    <button className="button" onClick={() => setCounter(counter1 + 1)}>Votar</button>
                </div>

                <div className="card-marie">
                    <span>Marie Curie</span><br/>
                    <span>Contagem de votos: {counter2}</span>
                    <button className="button" onClick={() => setCounter(counter2 + 1)}>Votar</button>
                </div>

                <div className="card-newton">
                    <span>Isaac Newton</span><br/>
                    <span>Contagem de votos: {counter3}</span>
                    <button className="button" onClick={() => setCounter(counter3 + 1)}>Votar</button>
                </div>
            </div>
        


        
    );
};
