import React,{ useState } from 'react';
import { Link } from 'react-router-dom';



import './style.css';


export default function Home(){
    const [counter, setCounter] = useState(0)

const increment = () =>{

    setCounter(counter + 1);
}
    
    return (
        <div className="container-flex">
            <div className="card-albert">
                <span>Albert E.</span><br/>
                <span>Contagem de votos: {counter}</span>
                <button className="button" onClick={increment}>Votar</button>
            </div>


        </div>
    );
};
