import React,{ useState } from 'react';
import { Link } from 'react-router-dom';
import Card from '../../components/Card.js';


import './style.css';


export default function Home(props){
     const [counter, setCounter] = useState(0);
     const [counter1, setCounter1] = useState(0)
     const [counter2, setCounter2] = useState(0);
     const [counter3, setCounter3] = useState(0);
     const [counter4, setCounter4] = useState(0);
     const [counter5, setCounter5] = useState(0);
    let votos = [];

    const armazenarVoto = (aux,aux1,aux2,aux3,aux4,aux5) => {

      votos.push(aux,aux1)
      console.log(votos)
    }
    return (
        <div className="body">    
            <div className="container-flex">
              <Card id="card-albert">
                  <span id="span-albert">Albert Einstein</span>
                  <span>Nº de votos:{counter}</span>
                  <button id="btn-albert" onClick={() => setCounter(counter + 1)}>Votar</button>
              </Card>
              
              <Card id="card-marie">
                <span id="span-marie">Marie Curie</span>
                <span>Nº de votos:{counter1}</span>
                <button id="btn-marie" onClick={() => setCounter1(counter1 + 1)}>Votar</button>
              
              
              </Card>

              <Card id="card-newton">
                <span id="span-newton">Isaac Newton</span>
                <span>Nº de votos:{counter2}</span>
                <button id="btn-newton" onClick={() => setCounter2(counter2 + 1)}>Votar</button>
              
              
              </Card>             
              <Card id="card-copernico">
                <span id="span-copernico">Nicolau Copernico</span>
                <span>Nº de votos:{counter3}</span>
                <button onClick={() => setCounter3(counter3 + 1)}>Votar</button>
              
              
              </Card>

              <Card id="card-null">
                <span id="span-null">Nulo</span>
                <span>Nº de votos:{counter4}</span>
                <button onClick={() => setCounter4(counter4 + 1)}>Votar</button>
              </Card>

              <Card id="card-white">
                <span id="span-white">Branco</span>
                <span>Nº de votos:{counter5}</span>
                <button id="btn-white" onClick={() => setCounter5(counter5 + 1)}>Votar</button>
              
              
              </Card>

             
            
            </div>
       
       <button onClick={() => armazenarVoto(counter,counter1)}>dale</button>
       
       </div>

        
    );
};
